mapper.insert:
	/*
		 SQL ID : insert
		 설  명 : 게시판 게시물의 정보 등록
 	*/
	INSERT INTO tb_brd_bbs_atcl (
	       ATCL_SN
	     , PAR_ATCL_SN
	     , ORG_CD
	     , BBS_CD
	     , HEAD_CD
	     , THUMB_FILE_SN
	     , ATCL_TITLE
	     , ATCL_CTS
	     , ATCL_TAG
	     , ATCL_LVL
	     , ATCL_ODR
	     , NOTICE_YN
	     , LOCK_YN
	     , EDITOR_YN
	     , HITS
	     , MOBILE_URL
  	     , ORIGIN_REG_NO
  	     , PASSWORD
  	     , CTGR_CD
	     , CRS_CRE_CD
	     , STAR_SCORE
	     , REG_NO
	     , REG_NM
	     , REG_DTTM
	     , MOD_NO
	     , MOD_DTTM
	) VALUES (
	       #{atclSn}
	     , #{parAtclSn}
	     , #{orgCd}
	     , #{bbsCd}
	     , #{headCd}
	     , #{thumbFileSn}
	     , #{atclTitle}
	     , #{atclCts}
	     , #{atclTag}
	     , IFNULL( FN_GET_NULL(#{atclLvl}) , 0)
	     , IFNULL(( SELECT MAX(IFNULL(FN_GET_NULL(ATCL_ODR),0)) + 1
	               FROM tb_brd_bbs_atcl AS SUBQUERY
	              WHERE BBS_CD = #{bbsCd}
	                AND IFNULL( FN_GET_NULL(PAR_ATCL_SN) ,1) = IFNULL( FN_GET_NULL(#{parAtclSn}),1) ),1)		     
	     , #{noticeYn}
	     , #{lockYn}
	     , #{editorYn}
	     , 0
	     , #{mobileUrl}
	     , #{originRegNo}
	     , #{password}
	     , #{ctgrCd}
	     , #{crsCreCd}
	     , #{starScore}
	     , #{regNo}
	     , #{regNm}
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	     , #{modNo}
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	)

	/*
		 SQL ID : insertService
		 설  명 : 게시판(서비스형) 게시물의 정보 등록
 	*/
	INSERT INTO tb_brd_bbs_atcl_service (
	       ATCL_SN
	     , BBS_CD
	     , SERVICE_REG_NM
		 , SERVICE_PW
		 , SERVICE_EMAIL
		 , SERVICE_TEL
		 , SERVICE_ENABLE_DATE
		 , SERVICE_ENABLE_TIME
		 , COMPANY_NAME
		 , HOPE_CURRICULUM
	     , REG_DTTM
	) VALUES (
	       #{atclSn}
	     , #{bbsCd}
	     , #{serviceRegNm}
	     , #{servicePw}
	     , #{serviceEmail}
	     , #{serviceTel}
	     , #{serviceEnableDate}
	     , #{serviceEnableTime}
	     , #{companyName}
	     , #{hopeCurriculum}
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	)

mapper.update:
	/*
		 SQL ID : update
		 설  명 : 게시판 게시물의 정보 수정
 	*/
	UPDATE tb_brd_bbs_atcl
	   SET HEAD_CD = #{headCd}
	     , THUMB_FILE_SN = #{thumbFileSn}
	     , ATCL_TITLE = #{atclTitle}
	     , ATCL_CTS = #{atclCts}
	     , ATCL_TAG = #{atclTag}
	     , ATCL_LVL = #{atclLvl}
	     , ATCL_ODR = #{atclOdr}
	     , NOTICE_YN = #{noticeYn}
	     , LOCK_YN = #{lockYn}
	     , EDITOR_YN = #{editorYn}
	     , MOBILE_URL = #{mobileUrl}
  	     , ORIGIN_REG_NO = #{originRegNo}
  	     , PASSWORD = #{password}
  	     , CRS_CRE_CD = #{crsCreCd}
  	     , STAR_SCORE = #{starScore}
  	     , CTGR_CD = #{ctgrCd}
	     , MOD_NO = #{modNo}
	     , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	 WHERE ATCL_SN = #{atclSn}

mapper.delete:
		/*
			 SQL ID : delete
			 설  명 : 게시판 게시물의 정보 삭제
	 	*/
		DELETE FROM tb_brd_bbs_atcl
         WHERE ATCL_SN = #{atclSn}

mapper.select:
/* SQL ID : selectAtclWithPreNext 설 명 : 게시판 게시물의 정보 조회(이전글 다음글 포함) */ WITH RECURSIVE CTE AS ( SELECT ATCL_SN , ATCL_SN AS PATH FROM tb_brd_bbs_atcl
AND PAR_ATCL_SN IS NULL
UNION ALL SELECT A.ATCL_SN, CONCAT(CTE.path,',',A.ATCL_SN) as path FROM tb_brd_bbs_atcl A inner join CTE on A.PAR_ATCL_SN = CTE.ATCL_SN
) SELECT C.* FROM ( SELECT ROWNUM AS RN, B.* ,LEAD(B.atclTitle, 1) OVER (ORDER BY B.ROWNUM ASC) AS nextAtclTitle ,LEAD(B.atclSn, 1) OVER (ORDER BY B.ROWNUM ASC) AS nextAtclSn ,LAG(B.atclTitle, 1) OVER (ORDER BY B.ROWNUM ASC) AS preAtclTitle ,LAG(B.atclSn, 1) OVER (ORDER BY B.ROWNUM ASC) AS preAtclSn FROM ( SELECT
FROM tb_brd_bbs_atcl A, CTE B WHERE A.ATCL_SN = B.ATCL_SN ORDER BY PATH ) B ) C WHERE C.atclSn = #{atclSn}

/* SQL ID : selectAtclWithPreNext 설 명 : 게시판 게시물의 정보 조회(이전글 다음글 포함) */ select *, FN_GET_LEAD_ATCL(T.ROWNUM-1,#{orgCd},#{bbsCd}) AS preAtclSn, (select atcl_title from tb_brd_bbs_atcl where ATCL_SN = (FN_GET_LEAD_ATCL(T.ROWNUM-1,#{orgCd},#{bbsCd}))) AS preAtclTitle, FN_GET_LEAD_ATCL(T.ROWNUM+1,#{orgCd},#{bbsCd}) AS nextAtclSn, (select atcl_title from tb_brd_bbs_atcl where ATCL_SN = (FN_GET_LEAD_ATCL(T.ROWNUM+1,#{orgCd},#{bbsCd}))) AS nextAtclTitle from (
SELECT a.ATCL_SN as atclSn , a.BBS_CD as bbsCd , (SELECT L.MENU_NM FROM tb_org_menu M INNER JOIN tb_org_menu_lang L ON M.ORG_CD = L.ORG_CD AND M.MENU_CD = L.MENU_CD WHERE M.ORG_CD = 'ORG0000001' AND M.OPTN_CTGR_CD_1 = 'BBS' AND M.OPTN_CTGR_CD_2 = a.BBS_CD AND L.LANG_CD = 'ko') as bbsNm , a.PAR_ATCL_SN as parAtclSn , a.HEAD_CD as headCd , ( SELECT HEAD_NM FROM tb_brd_bbs_head WHERE BBS_CD = a.BBS_CD AND HEAD_CD = a.HEAD_CD ) AS headNm , a.THUMB_FILE_SN as thumbFileSn , a.ATCL_TITLE as atclTitle , a.ATCL_CTS as atclCts , a.CTGR_CD as ctgrCd , a.ATCL_CTS AS atclCtsStr , a.ATCL_TAG as atclTag , a.ATCL_LVL as atclLvl , a.ATCL_ODR as atclOdr , a.NOTICE_YN as noticeYn , a.LOCK_YN as lockYn , a.HITS as hits , ( SELECT COUNT(*) FROM tb_brd_bbs_cmnt WHERE ATCL_SN = a.ATCL_SN) AS cmntCnt , a.EDITOR_YN as editorYn , a.MOBILE_URL as mobileUrl , a.REG_NO as regNo , ( SELECT FN_GET_USER_ID(a.REG_NO) FROM DUAL) AS regId , a.REG_NM as regNm , ( SELECT FN_GET_USER_NAME_MNG(a.REG_NO) FROM DUAL) AS regMngNm , a.REG_DTTM as regDttm , a.MOD_NO as modNo , a.MOD_DTTM as modDttm , CASE WHEN a.REG_DTTM >= DATE_FORMAT(DATE_ADD(NOW(),INTERVAL -1 DAY), '%Y%m%d%H%i%s') THEN 'NEW' ELSE '' END AS recently , a.ORIGIN_REG_NO as originRegNo , ( SELECT P.REG_NO FROM tb_brd_bbs_atcl P WHERE P.ATCL_SN = a.PAR_ATCL_SN) AS parRegNo , ( SELECT COUNT(1) FROM tb_brd_bbs_atcl C WHERE C.PAR_ATCL_SN = a.ATCL_SN) AS childCnt , a.STAR_SCORE AS starScore , a.CRS_CRE_CD AS crsCreCd , ( SELECT CRS_CRE_NM FROM tb_crs_cre_crs WHERE CRS_CRE_CD = a.CRS_CRE_CD ) AS crsCreNm , CONCAT_WS('>', b.ATCL_SN, a.ATCL_SN) path , ROW_NUMBER() OVER() AS ROWNUM FROM tb_brd_bbs_atcl a LEFT OUTER JOIN tb_brd_bbs_atcl b ON a.PAR_ATCL_SN = b.ATCL_SN WHERE a.ORG_CD = #{orgCd} AND a.BBS_CD = #{bbsCd} ORDER BY path
) T where T.atclSn = #{atclSn}
