	    ,  CLASS_STAT_5 	= #{classStat5}


	    ,  CLASS_STAT_6 	= #{classStat6}


	    ,  CLASS_STAT_7 	= #{classStat7}


	    ,  CLASS_STAT_8 	= #{classStat8}

   WHERE  CRS_CRE_CD 		= #{crsCreCd}									
      AND DATE_FORMAT(ATTEND_DTTM, '%Y%m%d') = DATE_FORMAT(#{attendDttm}, '%Y%m%d')				
      AND USER_NO = #{userNo}

