mapper.select:
	SELECT 
		COUNT(*)
	FROM tb_crs_cre_crs_resh A, tb_crs_resh_bank B		
	WHERE A.RESH_SN = B.RESH_SN							
	AND A.CRS_CRE_CD = #{crsCreCd}

    	AND A.USE_YN = #{useYn}			


    	AND A.REG_YN = #{regYn}

	SELECT 
		COUNT(*)
	FROM tb_crs_cre_crs_resh A, tb_crs_resh_item B
	WHERE A.RESH_SN = B.RESH_SN							
	AND A.CRS_CRE_CD = #{crsCreCd}

    	AND A.USE_YN = #{useYn}			


    	AND A.REG_YN = #{regYn}



    	AND A.USE_YN = #{useYn}			


    	AND A.REG_YN = #{regYn}			

	 ORDER  BY RESH_SN DESC




    	AND A.USE_YN = #{useYn}			


    	AND A.REG_YN = #{regYn}			


	ORDER  BY RESH_SN DESC

mapper.update:
UPDATE tb_crs_cre_crs_resh								
   SET USE_YN = #{useYn} 									
     , START_DTTM = #{startDttm} 							
     , END_DTTM = #{endDttm} 								
     , REG_YN = #{regYn} 									
     , MOD_NO = #{modNo} 									
     , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
 WHERE CRS_CRE_CD = #{crsCreCd} 							
   AND RESH_SN = #{reshSn}

mapper.delete:
	DELETE  FROM tb_crs_cre_crs_resh    	
   WHERE CRS_CRE_CD = #{crsCreCd} 			
    	AND RESH_SN = #{reshSn}
