mapper.select:
SELECT A.CRS_CRE_CD , A.ASMT_SN , A.SBJ_CD , (SELECT FN_GET_SBJ_NAME(A.SBJ_CD)) AS SBJ_NM , A.ASMT_TITLE , A.ASMT_CTS , A.ASMT_TYPE_CD , A.ASMT_SVC_CD , (SELECT FN_GET_CODE_NAME('ASMT_TYPE_CD', A.ASMT_TYPE_CD)) AS ASMT_TYPE_NM , A.ASMT_SELECT_TYPE_CD , (SELECT FN_GET_CODE_NAME('ASMT_SELECT_TYPE_CD', A.ASMT_SELECT_TYPE_CD)) AS ASMT_SELECT_TYPE_NM , CONCAT(SUBSTR(ASMT_START_DTTM,1,4),'.',SUBSTR(ASMT_START_DTTM,5,2),'.',SUBSTR(ASMT_START_DTTM,7,2)) AS ASMT_START_DTTM , CONCAT(SUBSTR(ASMT_END_DTTM,1,4),'.',SUBSTR(ASMT_END_DTTM,5,2),'.',SUBSTR(ASMT_END_DTTM,7,2)) AS ASMT_END_DTTM , IFNULL(CONCAT(DATE_FORMAT(STR_TO_DATE(A.ASMT_START_DTTM,'%Y%m%d%H%i%s'),'%Y.%m.%d %H:%i'),' ~ ',DATE_FORMAT(STR_TO_DATE(A.ASMT_END_DTTM,'%Y%m%d%H%i%s'),'%Y.%m.%d %H:%i')),'-') AS ASMT_DURATION , DATE_FORMAT(STR_TO_DATE(A.ASMT_START_DTTM,'%Y%m%d%H%i%s'),'%Y.%m.%d %H:%i') AS START_DTTM , DATE_FORMAT(STR_TO_DATE(A.ASMT_END_DTTM,'%Y%m%d%H%i%s'),'%Y.%m.%d %H:%i') AS END_DTTM , A.EXT_SEND_DTTM , A.ASMT_LIMIT_CNT , A.SEND_CRIT_PRGR_RATIO , A.REG_YN , (SELECT FN_GET_CODE_NAME('REG_YN',A.REG_YN)) AS REG_YN_NM , A.REG_NO , A.REG_DTTM , A.MOD_NO , A.MOD_DTTM , (SELECT FN_GET_USER_NAME(A.MOD_NO)) AS MOD_NM , (SELECT FN_GET_USER_NAME(A.REG_NO)) AS REG_NM , IFNULL(( (SELECT SUM(SEND.SCORE) FROM tb_lec_asmt_send SEND WHERE SEND.ASMT_SN = A.ASMT_SN AND SEND.RATE_YN = 'Y') / (SELECT COUNT(A.STD_NO) FROM tb_std_std A, tb_usr_user_info B, tb_usr_login C WHERE B.USER_NO = C.USER_NO AND A.USER_NO = B.USER_NO AND A.CRS_CRE_CD = #{crsCreCd} AND C.USER_STS = 'U' AND A.ENRL_STS IN ('S','C') ) ),0) AS AVG_SCORE , IFNULL((SELECT SUM(SEND.SCORE) FROM tb_lec_asmt_send SEND WHERE SEND.ASMT_SN = A.ASMT_SN AND SEND.STD_NO = #{stdNo} AND SEND.RATE_YN = 'Y' ),0) AS MY_SCORE FROM tb_lec_asmt A WHERE A.CRS_CRE_CD = #{crsCreCd}
AND A.SBJ_CD = (CASE WHEN #{sbjCd} = '' THEN A.SBJ_CD ELSE #{sbjCd} END)
AND A.ASMT_SVC_CD = #{asmtSvcCd}
ORDER BY ASMT_END_DTTM DESC, ASMT_START_DTTM DESC, A.ASMT_SN

mapper.update:

UPDATE  tb_lec_asmt 												
      SET  SBJ_CD 				= #{sbjCd}								
        ,  ASMT_TITLE 			= #{asmtTitle}							
        ,  ASMT_CTS 			= #{asmtCts}								
        ,  ASMT_TYPE_CD 		= #{asmtTypeCd}							
        ,  ASMT_SVC_CD 			= #{asmtSvcCd}							
        ,  ASMT_SELECT_TYPE_CD = #{asmtSelectTypeCd}      				
        ,  ASMT_START_DTTM 	= #{asmtStartDttm}          				
        ,  ASMT_END_DTTM 		= #{asmtEndDttm}							
        ,  EXT_SEND_DTTM 		= #{extSendDttm}							
        ,  ASMT_LIMIT_CNT 		= #{asmtLimitCnt}
        ,  SEND_CRIT_PRGR_RATIO = #{sendCritPrgrRatio}
        ,  REG_YN 				= #{regYn}								
        ,  MOD_NO 				= #{modNo}
        ,  MOD_DTTM 			= DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
    WHERE  CRS_CRE_CD 			= #{crsCreCd}								
 	   AND  ASMT_SN    			= #{asmtSn}

mapper.delete:

DELETE  FROM tb_lec_asmt			
    WHERE  CRS_CRE_CD  = #{crsCreCd}		
 	   AND  ASMT_SN = #{asmtSn}
