mapper.insert:
	/*
		 SQL ID : insert
		 설  명 : 사용량 로그 등록
 	*/
	MERGE INTO TB_LOG_USE_SIZE_LOG A
		  USING DUAL
		   ON (     A.ORG_CD = #{orgCd}
			    AND A.DIV_CD = #{divCd}
				AND DATE_FORMAT(STR_TO_DATE(A.SAVE_DTTM,'%Y%m%d%H%i%s'),'%Y%m%d') = DATE_FORMAT(NOW(),'%Y%m%d')
			  )
		  WHEN MATCHED THEN
	   	       UPDATE SET USE_SIZE = #{useSize}
		  WHEN NOT MATCHED THEN
	           INSERT (
	                  ORG_CD
	                , DIV_CD
	                , SAVE_DTTM
	                , USE_SIZE
	           ) VALUES (
	                  #{orgCd}
	                , #{divCd}
	                , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	                , #{useSize}
	          )
