mapper.sql:

		SELECT COUNT(*)
          FROM tb_usr_cnst
         WHERE USER_NO = #{userNo}

mapper.select:
		/*
			 SQL ID : select
			 설  명 : 사용자의 정보 조회
	 	*/

           AND A.USER_NO = #{userNo}

		/*
			 SQL ID : select
			 설  명 : 사용자의 정보 조회
	 	*/

          	AND B.USER_ID = #{userId}

		/*
			 SQL ID : select
			 설  명 : 사용자의 정보 조회
	 	*/

 		AND A.ORG_CD = #{orgCd}
		AND B.SNS_KEY = #{snsKey}
		AND B.SNS_DIV = #{snsDiv}

/* SQL ID : selectForLogin 설 명 : 사용자의 정보 조회 */
SELECT A.USER_NO as userNo , A.USER_NM as userNm , A.USER_NM_GANA as userNmGana , A.USER_NM_ENG as userNmEng , A.ORG_CD as orgCd , A.DEPT_CD as deptCd , B.USER_ID as userId , B.USER_PASS as userPass , B.ADMIN_LOGIN_ACPT_DIV_CD as adminLoginAcptDivCd , B.GPKI_INFO as gpkiInfo , B.LOGIN_FAIL_DTTM as loginFailDttm , B.LOGIN_FAIL_CNT as loginFailCnt , B.LAST_LOGIN_DTTM as lastLoginDttm , B.LOGIN_CNT as loginCnt , B.USER_STS as userSts , CASE WHEN NOW() >= STR_TO_DATE(IFNULL(FN_GET_NULL(B.PSWD_CHG_REQ_DTTM),FN_GET_NULL(B.MOD_DTTM)),'%Y%m%d%H%i%s') THEN 'Y' ELSE 'N' END as pswdChgReqYn , CASE WHEN B.LOGIN_FAIL_CNT >= FN_GET_CONFIG_VALUE('LOGIN','FAILCNT') THEN CASE WHEN DATE_FORMAT(NOW(), '%Y%m%d%H%i%s') - IFNULL(FN_GET_NULL(B.LOGIN_FAIL_DTTM), '19900101000001') < fn_get_config_value('LOGIN','GAPTIME') THEN 'N' ELSE 'Y' END ELSE 'Y' END AS loginUseYn , CASE WHEN (SELECT ITGRT_MBR_USE_YN FROM tb_org_org_info WHERE ORG_CD = #{orgCd}) = 'Y' THEN CASE WHEN (SELECT ITGRT_MBR_USE_YN FROM tb_org_org_info WHERE ORG_CD = A.ORG_CD) = 'Y' THEN 'Y' ELSE 'N' END ELSE 'N' END AS itgrtMbrUseYn , B.PHONE_VERI_YN FROM tb_usr_user_info A, tb_usr_login B WHERE A.USER_NO = B.USER_NO AND B.USER_STS = 'U'
AND B.USER_ID = #{userId}
AND A.ORG_CD = #{orgCd}
AND A.ORG_CD = #{orgCd}
AND B.SNS_KEY = #{snsKey} AND B.SNS_DIV = #{snsDiv}
AND B.SNS_KEY = #{snsKey} AND B.SNS_DIV = #{snsDiv} AND A.ORG_CD IN (SELECT ORG_CD FROM tb_org_org_info WHERE ITGRT_MBR_USE_YN = 'Y' ) AND ROWNUM = 1

/* SQL ID : selectForLogin 설 명 : 사용자의 정보 조회 */
SELECT A.USER_NO as userNo , A.USER_NM as userNm , A.USER_NM_GANA as userNmGana , A.USER_NM_ENG as userNmEng , A.ORG_CD as orgCd , B.USER_ID as userId , B.USER_PASS as userPass , B.ADMIN_LOGIN_ACPT_DIV_CD as adminLoginAcptDivCd , B.GPKI_INFO as gpkiInfo , B.LOGIN_FAIL_DTTM as loginFailDttm , B.LOGIN_FAIL_CNT as loginFailCnt , B.LAST_LOGIN_DTTM as lastLoginDttm , B.LOGIN_CNT as loginCnt , B.USER_STS as userSts , CASE WHEN NOW() >= STR_TO_DATE(IFNULL(FN_GET_NULL(B.PSWD_CHG_REQ_DTTM),FN_GET_NULL(B.MOD_DTTM)),'%Y%m%d%H%i%s') THEN 'Y' ELSE 'N' END as pswdChgReqYn , CASE WHEN B.LOGIN_FAIL_CNT >= FN_GET_CONFIG_VALUE('LOGIN','FAILCNT') THEN CASE WHEN DATE_FORMAT(NOW(), '%Y%m%d%H%i%s') - IFNULL(FN_GET_NULL(B.LOGIN_FAIL_DTTM), '19900101000001') < fn_get_config_value('LOGIN','GAPTIME') THEN 'N' ELSE 'Y' END ELSE 'Y' END AS loginUseYn , CASE WHEN (SELECT ITGRT_MBR_USE_YN FROM tb_org_org_info WHERE ORG_CD = #{orgCd}) = 'Y' THEN CASE WHEN (SELECT ITGRT_MBR_USE_YN FROM tb_org_org_info WHERE ORG_CD = A.ORG_CD) = 'Y' THEN 'Y' ELSE 'N' END ELSE 'N' END AS itgrtMbrUseYn FROM tb_usr_user_info A, tb_usr_login B WHERE A.USER_NO = B.USER_NO AND B.USER_STS = 'U'
AND B.USER_ID = #{userId}
AND A.ORG_CD = #{orgCd}
AND A.ORG_CD = #{orgCd}
AND B.SNS_KEY = #{snsKey} AND B.SNS_DIV = #{snsDiv}
AND B.SNS_KEY = #{snsKey} AND B.SNS_DIV = #{snsDiv} AND A.ORG_CD IN (SELECT ORG_CD FROM tb_org_org_info WHERE ITGRT_MBR_USE_YN = 'Y' ) AND ROWNUM = 1
AND B.USER_PASS = PASSWORD(#{userPass})

