mapper.delete:
	/*
		 SQL ID : delete
		 설  명 : 사용자의 정보 삭제
 	*/
	DELETE FROM tb_usr_user_info
	 WHERE USER_NO = #{userNo}

mapper.select:
		/*
			 SQL ID : selectSearchId
			 설  명 : 사용자의 이름과 이메일로 사용자 정보 검색
	 	*/
        SELECT B.USER_ID as userId
             , A.USER_NO as userNo
             , A.USER_NM as userNm
             , A.USER_NM_GANA as userNmGana
             , A.USER_NM_ENG as userNmEng
             , A.EMAIL as email
          FROM tb_usr_user_info A, tb_usr_login B
         WHERE A.USER_NO = B.USER_NO
           AND B.USER_STS = 'U'

		   AND A.USER_NM = #{userNm}


           AND B.USER_ID = #{userId}


		   AND A.ORG_CD = #{orgCd}


           AND A.EMAIL = #{email}
          	LIMIT 1;

/* SQL ID : selectSearchPass 설 명 : 사용자의 패스워드를 조회한다. */ SELECT TB.userPass FROM (SELECT B.USER_PASS as userPass , ROW_NUMBER() OVER() as ROWNUM FROM tb_usr_user_info A, tb_usr_login B WHERE A.USER_NO = B.USER_NO AND B.USER_STS = 'U'
AND A.USER_NO = #{userNo}
AND A.ORG_CD = #{orgCd}
AND B.USER_PASS = PASSWORD(#{userPass})) as TB WHERE TB.ROWNUM = 1

SELECT A.USER_NO, A.DEPT_CD, B.USER_STS
FROM tb_usr_user_info A, tb_usr_login B
WHERE A.USER_NO = B.USER_NO
	AND B.USER_STS = 'U'
	AND B.USER_ID = #{userId}
	AND A.ORG_CD = #{orgCd}

SELECT CASE WHEN COUNT(*) = 0 THEN 'Y' ELSE 'N' END AS isUseable
  FROM tb_usr_user_info
 WHERE EMAIL = #{email}

