mapper.select:

		SELECT COUNT(1) as CNT
          FROM TB_USR_USER_INFO A, TB_USR_LOGIN B
         WHERE A.USER_NO = B.USER_NO

		AND B.SNS_KEY = #{snsKey}
		AND B.SNS_DIV = #{snsDiv}

