mapper.update:
	UPDATE  tb_crs_asmt_qbank_qstn 								
      SET  QSTN_TITLE 	= #{qstnTitle}          					
        ,	QSTN_CTS  	= #{qstnCts}          					
        ,	MOD_NO    	= #{modNo}          						
        ,	MOD_DTTM  	= DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')

    WHERE 	SBJ_CD 		= #{sbjCd}								
      AND 	CTGR_CD 	= #{ctgrCd}								
      AND 	QSTN_SN 	= #{qstnSn}
