		 설  명 : 게시판 댓글의 전체 카운트
 	*/
 	SELECT COUNT(*) FROM (

 	) T

	/*
		 SQL ID : select
		 설  명 : 게시판 댓글의 정보 조회
 	*/

 	   AND A.CMNT_SN = #{cmntSn}

mapper.insert:
	/*
		 SQL ID : insert
		 설  명 : 게시판 댓글의 정보 등록
 	*/
	INSERT INTO tb_brd_bbs_cmnt (
	       CMNT_SN
	     , ATCL_SN
	     , CMNT_CTS
	     , EMOTICON_NO
	     , REG_NM
	     , REG_NO
	     , REG_DTTM
	     , MOD_NO
	     , MOD_DTTM
	) VALUES (
	       #{cmntSn}
	     , #{atclSn}
	     , #{cmntCts}
	     , #{emoticonNo}
	     , ( SELECT FN_GET_USER_NAME(#{regNo}) FROM DUAL)
	     , #{regNo}
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	     , #{modNo}
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	)

mapper.update:
	/*
		 SQL ID : update
		 설  명 : 게시판 댓글의 정보 수정
 	*/
	UPDATE tb_brd_bbs_cmnt
	   SET CMNT_CTS = #{cmntCts}
	     , EMOTICON_NO = #{emoticonNo}
		 , MOD_NO = #{modNo}
		 , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	 WHERE CMNT_SN = #{cmntSn}
	   AND ATCL_SN = #{atclSn}

mapper.delete:
		/*
			 SQL ID : delete
			 설  명 : 게시판 댓글의 정보 삭제
	 	*/
		DELETE FROM tb_brd_bbs_cmnt
         WHERE CMNT_SN = #{cmntSn}

		/*
			 SQL ID : delete
			 설  명 : 게시판 게시글 전체 댓글의 정보 삭제
	 	*/
		DELETE FROM tb_brd_bbs_cmnt
         WHERE ATCL_SN = #{atclSn}
