mapper.update:
   UPDATE tb_crs_crs_bbs_cmnt									
	   SET CMNT_CTS			= #{cmntCts}						
		 , EMOTICON_NO	= #{emoticonNo}							
		 , MOD_NO		= #{modNo}								
		 , MOD_DTTM	= DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	 WHERE CRS_CD		= #{crsCd}								
	   AND BBS_CD		= #{bbsCd}								
	   AND ATCL_SN		= #{atclSn}								
	   AND CMNT_SN		= #{cmntSn}

mapper.delete:
   DELETE FROM tb_crs_crs_bbs_cmnt								
	 WHERE CRS_CD		= #{crsCd}								
	   AND BBS_CD		= #{bbsCd}								
	   AND ATCL_SN		= #{atclSn}								
	   AND CMNT_SN		= #{cmntSn}

   DELETE FROM tb_crs_crs_bbs_cmnt								
	 WHERE CRS_CD		= #{crsCd}								
	   AND BBS_CD		= #{bbsCd}								
	   AND ATCL_SN		= #{atclSn}
