mapper.insert:
INSERT INTO tb_crs_crs_ofln_sbj (  
       CRS_CD  
     , SBJ_CD  
     , SBJ_ODR  
     , REG_NO  
     , REG_DTTM  
     , MOD_NO  
     , MOD_DTTM  
     , EDU_MTHD_CD  
) VALUES (  
       #{crsCd}  
     , #{sbjCd}  
     , NVL(( SELECT MAX(NVL((SBJ_ODR),0)) + 1 
               FROM tb_crs_crs_ofln_sbj AS SUBQUERY
              WHERE CRS_CD = #{crsCd}),1)  
     , #{regNo}  
     , NVL((#{regDttm}), DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'))
     , #{modNo}  
     , NVL((#{modDttm}), DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'))  
     , #{eduMthdCd}  
)

mapper.update:
UPDATE tb_crs_crs_ofln_sbj  
   SET SBJ_ODR = #{sbjOdr}  
     , MOD_NO = #{modNo}  
     , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s') 
     , EDU_MTHD_CD = #{eduMthdCd}  
 WHERE CRS_CD = #{crsCd}  
   AND SBJ_CD = #{sbjCd}

update.foreach:
  UPDATE tb_crs_crs_ofln_sbj  
  SET
	     SBJ_ODR = #{item.sbjOdr}  
       , MOD_NO = #{item.modNo}  
       , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')  
       , EDU_MTHD_CD = #{item.eduMthdCd} 	
   WHERE CRS_CD = #{item.crsCd}  
     AND SBJ_CD = #{item.sbjCd}

mapper.delete:
DELETE FROM tb_crs_crs_ofln_sbj  
 WHERE CRS_CD = #{crsCd}  
   AND SBJ_CD = #{sbjCd}

mapper.select:
SELECT COUNT(*) FROM tb_crs_ofln_sbj A, tb_crs_sbj_ctgr B WHERE A.SBJ_CTGR_CD = B.SBJ_CTGR_CD AND A.SBJ_CD NOT IN (SELECT SBJ_CD FROM tb_crs_crs_ofln_sbj WHERE CRS_CD = #{crsCd} )
AND A.ORG_CD = #{orgCd}
AND A.SBJ_NM like CONCAT('%', #{sbjNm}, '%')
