mapper.insert:
INSERT INTO tb_crs_crs_onln_sbj (  
       CRS_CD  
     , SBJ_CD  
     , STUDY_MTHD  
     , SBJ_ODR  
     , REG_NO  
     , REG_DTTM  
     , MOD_NO  
     , MOD_DTTM  
 ) VALUES (  
       #{crsCd}  
     , #{sbjCd}  
     , #{studyMthd}  
     , NVL(( SELECT MAX(NVL((SBJ_ODR),0)) + 1 
               FROM tb_crs_crs_onln_sbj AS SUBQUERY
              WHERE CRS_CD = #{crsCd}),1)  
     , #{regNo}  
     , NVL((#{regDttm}), DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')) 
     , #{modNo}  
     , NVL((#{modDttm}), DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'))
 )

update.foreach:
  UPDATE tb_crs_crs_onln_sbj  
  SET
	     STUDY_MTHD = #{item.studyMthd}  
       , SBJ_ODR = #{item.sbjOdr}  
       , MOD_NO = #{item.modNo}  
       , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
   WHERE CRS_CD = #{item.crsCd}  
     AND SBJ_CD = #{item.sbjCd}

mapper.update:
  UPDATE tb_crs_crs_onln_sbj  
  SET
	     STUDY_MTHD = #{studyMthd}  
       , MOD_NO = #{modNo}  
       , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
   WHERE CRS_CD = #{crsCd}  
     AND SBJ_CD = #{sbjCd}

mapper.delete:
DELETE FROM tb_crs_crs_onln_sbj  
 WHERE CRS_CD = #{crsCd}  
   AND SBJ_CD = #{sbjCd}

mapper.select:
SELECT COUNT(*) FROM tb_crs_onln_sbj A, tb_crs_sbj_ctgr B WHERE A.SBJ_CTGR_CD = B.SBJ_CTGR_CD AND A.USE_YN = 'Y' AND A.SBJ_CD NOT IN (SELECT SBJ_CD FROM tb_crs_crs_onln_sbj WHERE CRS_CD = #{crsCd} )
AND A.ORG_CD = #{orgCd}
AND A.SBJ_NM like CONCAT('%',#{sbjNm},'%')

mapper.insert:

INSERT INTO tb_cro_open_crs_sbj (  
       CRS_CD  
     , SBJ_CD  
     , SBJ_ODR  
     , REG_NO  
     , REG_DTTM  
     , MOD_NO  
     , MOD_DTTM  
) VALUES (  
       #{crsCd}  
     , #{sbjCd}  
     , NVL(( SELECT MAX(NVL((SBJ_ODR),0)) + 1  
               FROM tb_cro_open_crs_sbj AS SUBQUERY
              WHERE CRS_CD = #{crsCd}),1)  
     , #{regNo}  
     , NVL((#{regDttm}), DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')) 
     , #{modNo}  
     , NVL((#{modDttm}), DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'))
)

mapper.select:

     AND A.SBJ_CD NOT IN (SELECT SBJ_CD FROM tb_cro_open_crs_sbj 
		   					WHERE CRS_CD = #{crsCd} )  

    	AND A.SBJ_CTGR_CD = #{sbjCtgrCd}  


    	AND A.ORG_CD = #{orgCd} 	


    	AND A.SBJ_NM like CONCAT('%', #{sbjNm}, '%') 

   ORDER BY SBJ_NM
