mapper.select:
SELECT CTGR_CD , CTGR_NM , ORG_CD , REG_NO , REG_DTTM , MOD_NO , MOD_DTTM , ( SELECT COUNT(*) FROM tb_crs_exam_qstn WHERE CTGR_CD = A.CTGR_CD) AS SUB_CNT FROM tb_crs_exam_ctgr A WHERE CTGR_LVL = 1
AND ORG_CD = #{orgCd}
ORDER BY CTGR_NM

SELECT CTGR_CD , CTGR_NM , CTGR_DESC , CRS_NM , USE_YN , ORG_CD , REG_NO , REG_DTTM , MOD_NO , MOD_DTTM , ( SELECT COUNT(*) FROM tb_crs_exam_qstn WHERE CTGR_CD = A.CTGR_CD) AS SUB_CNT FROM tb_crs_exam_ctgr A WHERE CTGR_LVL = 2 AND PAR_CTGR_CD = #{parCtgrCd}
AND USE_YN = #{useYn}
AND ORG_CD = #{orgCd}
ORDER BY CTGR_NM

mapper.insert:
 	INSERT 	INTO tb_crs_exam_ctgr (			
		 	CTGR_CD	
		 ,  CTGR_LVL		
		 ,  PAR_CTGR_CD						
		 ,	CTGR_NM
		 ,	CTGR_DESC
		 ,	USE_YN
		 ,	CRS_NM	
		 ,  ORG_CD								
		 ,	REG_NO									
		 ,	REG_DTTM								
	) VALUES ( 										
		 	#{ctgrCd}									
		 ,  #{ctgrLvl}
		 ,  #{parCtgrCd}
		 ,	#{ctgrNm}	
		 ,	#{ctgrDesc}	
		 ,	#{useYn}	
		 ,	#{crsNm}	
		 ,  #{orgCd}
		 ,	#{regNo}									
		 ,	DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')	)

mapper.update:
	UPDATE  tb_crs_exam_ctgr 									
	   SET	
	   		CTGR_NM 	= #{ctgrNm}	

		   	 ,	CTGR_DESC	= #{ctgrDesc}
		   	 ,	USE_YN		= #{useYn}
		   	 ,	CRS_NM		= #{crsNm}	

		 ,	MOD_NO		= #{modNo}									
		 ,	MOD_DTTM 	= DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')	
  	 WHERE 	CTGR_CD 	= #{ctgrCd}

UPDATE tb_crs_exam_ctgr SET 
	CTGR_NM = #{ctgrNm} 
	WHERE PAR_CTGR_CD = #{ctgrCd}

mapper.select:
SELECT CTGR_CD 
	FROM tb_crs_exam_ctgr 
	WHERE PAR_CTGR_CD = #{ctgrCd}
