mapper.select:
SELECT A.TITLE , B.CTGR_NM , B.CRS_NM , A.QSTN_SN , A.CTGR_CD , A.QSTN_TYPE , (SELECT CODE_NM FROM tb_sys_code WHERE CODE_CTGR_CD = 'EXAM_QSTN_TYPE' AND CODE_CD = A.QSTN_TYPE ) AS QSTN_TYPE_NM , A.QSTN_CTS , A.VIEW_1 , A.VIEW_2 , A.VIEW_3 , A.VIEW_4 , A.VIEW_5 , A.RGT_ANSR , A.MULTI_RGT_ANSR , A.QSTN_EXPL , A.ORG_CD , A.REG_NO , A.REG_DTTM , A.MOD_NO , A.MOD_DTTM , (SELECT FN_GET_USER_NAME(A.MOD_NO)) AS MOD_NM , (SELECT FN_GET_USER_NAME(A.REG_NO)) AS REG_NM FROM tb_crs_exam_qstn A LEFT OUTER JOIN tb_crs_exam_ctgr B ON (A.CTGR_CD = B.CTGR_CD) WHERE 1 = 1
AND B.PAR_CTGR_CD = #{parCtgrCd }
AND A.CTGR_CD = #{ctgrCd }
AND A.QSTN_TYPE = #{qstnType}
AND B.ORG_CD = #{orgCd }
AND A.TITLE LIKE CONCAT('%', #{searchKey}, '%')
AND B.USE_YN = #{useYn}
ORDER BY QSTN_TYPE_NM DESC

	SELECT 
		COUNT(*)
	FROM  tb_crs_exam_qstn A
     LEFT OUTER JOIN
     		tb_crs_exam_ctgr B
     ON (A.CTGR_CD = B.CTGR_CD)									
    WHERE 1 = 1

     AND B.PAR_CTGR_CD = #{ctgrCd }	


     AND A.CTGR_CD = #{parCtgrCd }	


     AND A.QSTN_TYPE = #{qstnType}	


     AND B.ORG_CD = #{orgCd }	


     AND A.TITLE LIKE CONCAT('%', #{searchKey}, '%')

SELECT A.TITLE , B.CTGR_NM , B.CRS_NM , A.QSTN_SN , A.CTGR_CD , A.QSTN_TYPE , (SELECT CODE_NM FROM tb_sys_code WHERE CODE_CTGR_CD = 'EXAM_QSTN_TYPE' AND CODE_CD = A.QSTN_TYPE ) AS QSTN_TYPE_NM , A.QSTN_CTS , A.VIEW_1 , A.VIEW_2 , A.VIEW_3 , A.VIEW_4 , A.VIEW_5 , A.RGT_ANSR , A.MULTI_RGT_ANSR , A.QSTN_EXPL , A.REG_NO , A.REG_DTTM , A.MOD_NO , A.MOD_DTTM , (SELECT FN_GET_USER_NAME(A.MOD_NO)) AS MOD_NM , (SELECT FN_GET_USER_NAME(A.REG_NO)) AS REG_NM FROM tb_crs_exam_qstn A LEFT OUTER JOIN tb_crs_exam_ctgr B ON (A.CTGR_CD = B.CTGR_CD) WHERE 1 = 1
AND B.PAR_CTGR_CD = #{ctgrCd }
AND A.CTGR_CD = #{parCtgrCd }
AND A.QSTN_TYPE = #{qstnType}
AND B.ORG_CD = #{orgCd }
AND A.TITLE LIKE CONCAT('%', #{searchKey}, '%')
ORDER BY QSTN_TYPE_NM DESC
