mapper.insert:
 	INSERT  INTO tb_crs_ofln_sbj (				
       	ORG_CD								
        ,  SBJ_CD								
        ,  SBJ_NM								
        ,  SBJ_DESC							
        ,  SBJ_CTGR_CD							
        ,  USE_YN								
        ,  REG_NO								
        ,  REG_DTTM							
	) VALUES ( 									
       	#{orgCd}	
		,  #{sbjCd}
        ,  #{sbjNm}								
        ,  #{sbjDesc}							
        ,  #{sbjCtgrCd}							
        ,  #{useYn}								
        ,  #{regNo}								
        ,  DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')	
             )

mapper.update:
	UPDATE  tb_crs_ofln_sbj 											
     SET  SBJ_NM 				= #{sbjNm}								
       ,  SBJ_DESC 			= #{sbjDesc}								
       ,  SBJ_CTGR_CD 		= #{sbjCtgrCd}							
       ,  USE_YN				= #{useYn}								
       ,  MOD_NO				= #{modNo}								
       ,  MOD_DTTM 			= DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')	
   WHERE  SBJ_CD 				= #{sbjCd}
