           AND BNNR_SN = #{bnnrSn}

mapper.insert:
	/*
		 SQL ID : insert
		 설  명 : 베너의 정보 등록
 	*/
	INSERT INTO tb_etc_bnnr (
	       BNNR_SN
	     , ORG_CD
	     , BNNR_NM
	     , BNNR_URL
	     , BNNR_POS_CD
	     , BNNR_ODR
	     , BNNR_TRGT
	     , BNNR_IMG_SN
	     , BNNR_DESC
	     , OPEN_YN
	     , REG_NO
	     , REG_DTTM
	     , MOD_NO
	     , MOD_DTTM
	) VALUES (
	       #{bnnrSn}
	     , #{orgCd} 
	     , #{bnnrNm}  
	     , #{bnnrUrl}  
	     , #{bnnrPosCd}  
	     , ( SELECT NVL((MAX(BNNR_ODR)),0)+1 
	           FROM tb_etc_bnnr AS SUBQUERY  
	          WHERE ORG_CD = #{orgCd} )  
	     , #{bnnrTrgt}  
	     , #{bnnrImgSn}  
	     , #{bnnrDesc}  
	     , #{openYn}  
	     , #{regNo}  
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s') 
	     , #{modNo}  
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s') 
	)

mapper.update:
	/*
		 SQL ID : update
		 설  명 : 베너의 정보 수정
 	*/
	UPDATE tb_etc_bnnr
	   SET BNNR_NM = #{bnnrNm}
	     , ORG_CD = #{orgCd}
	     , BNNR_URL = #{bnnrUrl}
	     , BNNR_POS_CD = #{bnnrPosCd}
	     , BNNR_ODR = #{bnnrOdr}
	     , BNNR_TRGT = #{bnnrTrgt}
	     , BNNR_IMG_SN  = #{bnnrImgSn}
	     , BNNR_DESC = #{bnnrDesc}
	     , OPEN_YN = #{openYn}
	     , MOD_NO = #{modNo}
	     , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	 WHERE BNNR_SN = #{bnnrSn}

mapper.delete:
	/*
		 SQL ID : delete
		 설  명 : 베너의 정보 삭제
 	*/
 	DELETE FROM tb_etc_bnnr 
 	 WHERE BNNR_SN = #{bnnrSn}
