mapper.sql:
WHERE 1=1

   AND CRS_CD = #{crsCd}


   AND CLASS_AGENT_PK = #{classAgentPk}


   AND SYNC_STATUS = #{syncStatus}


	AND SYNC_STATUS = #{searchValue}

mapper.select:
/* SQL ID : listPageingCreInfo 설 명 : 년도 기수별 전송 데이터 페이징 목록 */
AND CLASS_AGENT_PK like CONCAT('%',#{searchValue},'%')
ORDER BY MOD_DTTM DESC

mapper.update:
	/*
		 SQL ID : updateHrdApiCre
		 설  명 : api 년도 기수별 전송 데이터  수정
 	*/
	UPDATE tb_hrd_crs_cre_crs
	   SET MOD_DTTM         =   DATE_FORMAT( NOW(), '%Y%m%d%H%i%s')  

	     , SYNC_RESULT_MSG  =   #{syncResultMsg}


	     , MOD_NO			=   #{modNo}


		 , SYNC_STATUS      =   #{syncStatus}

	WHERE CRS_CD = #{crsCd}

	   AND CLASS_AGENT_PK = #{classAgentPk}


	   AND SYNC_STATUS = #{searchValue}


		AND NUM = #{num}

mapper.insert:
  INSERT INTO tb_hrd_crs_cre_crs (  
        CRS_CD  
       , AGENT_PK  
       , SEQ 
       , COURSE_AGENT_PK  
       , CLASS_AGENT_PK
       , FULL_SCORE
       , START_DT  
       , END_DT  
       , CHANGE_STATE  
       , REG_DATE  
       , TRACSE_TME 
       , CRS_YEAR  
       , CRS_TERM  
       , SYNC_STATUS  
       , SYNC_RESULT_MSG 
       , REG_NO
       , REG_DTTM
       , MOD_NO
       , MOD_DTTM
 ) VALUES (  
        #{crsCd}  
       , 'edulife'  
       , #{seq}  
       , #{courseAgentPk}  
       , #{classAgentPk}  
       , 100  
       , #{startDt}  
       , #{endDt}  
       , #{changeState}  
       , DATE_FORMAT( NOW(), '%Y%m%d%H%i%s')   
       , #{tracseTme}  
       , #{crsYear}  
       , #{crsTerm}  
       , 'W' 
       , #{syncResultMsg}  
       , #{regNo}  
       , NVL((#{regDttm}), DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'))  
       , #{modNo}  
       , NVL((#{modDttm}), DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'))  
)
