mapper.sql:
WHERE 1=1

   AND A.USER_AGENT_PK = #{userAgentPk}


   AND A.NUM = #{num}


   AND A.SYNC_STATUS = #{syncStatus}


	AND DATE_FORMAT(A.REG_DTTM , '%Y%m%d') BETWEEN DATE_FORMAT(#{searchFrom} , '%Y%m%d') AND DATE_FORMAT(#{searchTo} , '%Y%m%d')

mapper.insert:
	/*
		 SQL ID : insertUserInfo
		 설  명 : api 년도 기수별 전송 데이터  등록
 	*/
	INSERT INTO tb_hrd_usr_user_info (
		  AGENT_PK
		, SEQ
		, USER_AGENT_PK
		, USER_NAME
		, RES_NO
		, ENC_RES_NO
		, EMAIL
		, MOBILE
		, CHANGE_STATE
		, REG_DATE
		, NW_INO
		, TRNEE_SE
		, IRGLBR_SE
		, SYNC_STATUS
		, SYNC_RESULT_MSG
		, REG_NO
		, REG_DTTM
		, MOD_NO
		, MOD_DTTM
	) VALUES (
		  #{agentPk}
		, #{seq}
		, #{userAgentPk}
		, #{userName}
		, #{resNo}
		, #{encResNo}
		, #{email}
		, #{mobile}
		, #{changeState}
		, #{regDate}
		, #{nwIno}
		, #{trneeSe}
		, #{irglbrSe}
	    , #{syncStatus}
	    , #{syncResultMsg} 
	    , #{regNo}
	    , DATE_FORMAT( NOW(), '%Y%m%d%H%i%s') 
	    , #{modNo}
	    , DATE_FORMAT( NOW(), '%Y%m%d%H%i%s') 
	)

mapper.update:
	/*
		 SQL ID : updateUserInfo
		 설  명 : api 년도 기수별 전송 데이터  수정
 	*/
	UPDATE tb_hrd_usr_user_info
	   SET MOD_DTTM         =   DATE_FORMAT( NOW(), '%Y%m%d%H%i%s')  

		 , SYNC_RESULT_MSG  =   #{syncResultMsg}


		 , MOD_NO			=   #{modNo}


		 , SEQ			=   #{seq}


		 , SYNC_STATUS      =   #{syncStatus}


		 , USER_NAME      =   #{userName}


		 , RES_NO      =   #{resNo}


		 , ENC_RES_NO      =   #{encResNo}


		 , EMAIL      =   #{email}


		 , MOBILE      =   #{mobile}


		 , CHANGE_STATE      =   #{changeState}


		 , NW_INO      =   #{nwIno}


		 , TRNEE_SE      =   #{trneeSe}


		 , IRGLBR_SE      =   #{irglbrSe}


		 , REG_DATE      =   #{regDate}

	WHERE 1 = 1
	AND USER_AGENT_PK = #{userAgentPk}

		AND NUM = #{num}


	   AND SYNC_STATUS = #{searchValue}

mapper.delete:
	/*
		 SQL ID : deleteUserInfo
		 설  명 : api 년도 기수별 전송 데이터  삭제
 	*/
	DELETE FROM tb_hrd_usr_user_info
	WHERE USER_AGENT_PK = #{userAgentPk}

		AND NUM = #{num}

mapper.select:
SELECT A.NUM , A.AGENT_PK , @row_num := @row_num + 1 as SEQ , A.USER_AGENT_PK , A.USER_NAME , A.RES_NO , A.ENC_RES_NO , A.EMAIL , A.MOBILE , A.CHANGE_STATE , DATE_FORMAT( NOW(), '%Y-%m-%d %H:%i:%s') as REG_DATE , A.NW_INO , A.TRNEE_SE , A.IRGLBR_SE FROM tb_hrd_usr_user_info A, (select @row_num := 0) as B WHERE 1=1 AND A.SYNC_STATUS != 'S'
AND A.USER_AGENT_PK = #{userAgentPk}
AND A.NUM = #{num}
AND DATE_FORMAT(A.REG_DTTM , '%Y%m%d') BETWEEN DATE_FORMAT(#{searchFrom} , '%Y%m%d') AND DATE_FORMAT(#{searchTo} , '%Y%m%d')
AND A.SYNC_STATUS = #{searchValue}
