mapper.select:


AND #{strExamQstnSn} LIKE CONCAT('%@#', EXAM_QSTN_SN, '@#%')

ORDER BY RAND()
LIMIT #{examSetCnt}

mapper.insert:

INSERT  INTO tb_lec_exam_qstn (				
        	CRS_CRE_CD							
        ,  EXAM_SN								
        ,  EXAM_QSTN_SN						
        ,  QSTN_NO								
        ,  SUB_NO								
        ,  QSTN_TYPE							
        ,  TITLE								
        ,  QSTN_CTS							
        ,  EMPL_1								
        ,  EMPL_2								
        ,  EMPL_3								
        ,  EMPL_4								
        ,  EMPL_5								
        ,  RGT_ANSR							
        ,  MULTI_RGT_ANSR						
        ,  QSTN_EXPL							
        ,  QSTN_SCORE							
        ,  EDITOR_YN							
        ,  REG_NO								
        ,  REG_DTTM							
        ,  MOD_NO								
        ,  MOD_DTTM  )							
	SELECT  #{crsCreCd}							
        ,  #{examSn}		
		,#{examQstnSn}
        ,  #{qstnNo}								
        ,  NVL((MAX(SUB_NO)),0) + 1 		
        ,  #{qstnType}							
        ,  #{title}								
        ,  ''									
        ,  #{empl1}								
        ,  #{empl2}								
        ,  #{empl3}								
        ,  #{empl4}								
        ,  #{empl5}								
        ,  #{rgtAnsr}							
        ,  #{multiRgtAnsr}						
        ,  #{qstnExpl}							
        ,  #{qstnScore}
        ,  #{editorYn}	
        ,  #{regNo}								
        ,  DATE_FORMAT(NOW(), '%Y%m%d%H%i%s') 	
        ,  #{modNo}								
        ,  DATE_FORMAT(NOW(), '%Y%m%d%H%i%s') 	
     FROM  tb_lec_exam_qstn					
    WHERE  CRS_CRE_CD 	= #{crsCreCd}				
      AND  EXAM_SN 	= #{examSn}				
      AND  QSTN_NO 	= #{qstnNo}

mapper.update:

UPDATE 	tb_lec_exam_qstn										
      SET 	QSTN_NO			= #{qstnNo}								
        ,  SUB_NO			= #{subNo}								
        ,  QSTN_TYPE      	= #{qstnType}								
        ,  TITLE       	= #{title}								
        ,  QSTN_CTS       	= #{qstnCts}								
        ,  EMPL_1         	= #{empl1}								
        ,  EMPL_2         	= #{empl2}
        ,  EMPL_3         	= #{empl3}								
        ,  EMPL_4         	= #{empl4}
        ,  EMPL_5         	= #{empl5}
        ,  RGT_ANSR       	= #{rgtAnsr}								
        ,  MULTI_RGT_ANSR	= #{multiRgtAnsr}							
        ,  QSTN_EXPL      	= #{qstnExpl}								
        ,  QSTN_SCORE     	= #{qstnScore}
        ,  EDITOR_YN     	= #{editorYn}								
        ,  MOD_NO         	= #{modNo}								
        ,  MOD_DTTM       	= DATE_FORMAT(NOW(), '%Y%m%d%H%i%s') 
    WHERE  CRS_CRE_CD     	= #{crsCreCd}								
      AND  EXAM_SN        	= #{examSn}								
      AND  EXAM_QSTN_SN   	= #{examQstnSn}


UPDATE tb_lec_exam_qstn  
     SET QSTN_SCORE	= #{qstnScore}  
       , MOD_NO = #{modNo}  
       , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')  
   WHERE CRS_CRE_CD = #{crsCreCd}  
     AND EXAM_SN = #{examSn}  
     AND EXAM_QSTN_SN = #{examQstnSn}

update.foreach:
	UPDATE 	tb_lec_exam_qstn	
	SET								
     	QSTN_NO			= #{item.qstnNo}								
        ,  SUB_NO			= #{item.subNo}								
        ,  QSTN_TYPE      	= #{item.qstnType}								
        ,  TITLE       	= #{item.title}								
        ,  QSTN_CTS       	= #{item.qstnCts}								
        ,  EMPL_1         	= #{item.empl1}								
        ,  EMPL_2         	= #{item.empl2}
        ,  EMPL_3         	= #{item.empl3}								
        ,  EMPL_4         	= #{item.empl4}								
        ,  EMPL_5         	= #{item.empl5}								
        ,  RGT_ANSR       	= #{item.rgtAnsr}								
        ,  MULTI_RGT_ANSR	= #{item.multiRgtAnsr}							
        ,  QSTN_EXPL      	= #{item.qstnExpl}								
        ,  QSTN_SCORE     	= #{item.qstnScore}
        ,  EDITOR_YN     	= #{item.editorYn}
        ,  MOD_NO         	= #{item.modNo}								
        ,  MOD_DTTM       	= DATE_FORMAT(NOW(), '%Y%m%d%H%i%s') 	
    WHERE  CRS_CRE_CD     	= #{item.crsCreCd}								
      AND  EXAM_SN        	= #{item.examSn}								
      AND  EXAM_QSTN_SN   	= #{item.examQstnSn}

mapper.delete:

DELETE  FROM tb_lec_exam_qstn			
    WHERE  CRS_CRE_CD		= #{crsCreCd}     
      AND  EXAM_SN			= #{examSn}       
      AND  EXAM_QSTN_SN	= #{examQstnSn}


DELETE  FROM tb_lec_exam_qstn	
    WHERE  CRS_CRE_CD	= #{crsCreCd}	
      AND  EXAM_SN		= #{examSn}

mapper.select:

SELECT  NVL((MAX(QSTN_NO)),0) AS QSTN_NO	
     FROM  tb_lec_exam_qstn          		
  	 WHERE  CRS_CRE_CD	= #{crsCreCd}      	
      AND  EXAM_SN		= #{examSn}


SELECT NVL((MAX(QSTN_NO)),0) + 1 AS QSTN_NO 			
	    FROM  tb_lec_exam_qstn				
	    WHERE  CRS_CRE_CD     	= #{crsCreCd}								
	      AND  EXAM_SN        	= #{examSn}


SELECT  NVL((QSTN_NO)) AS QSTN_NO 	
     FROM  tb_lec_exam_qstn          		
  	 WHERE  CRS_CRE_CD	= #{crsCreCd}      	
      AND  EXAM_SN		= #{examSn}

mapper.update:

UPDATE tb_lec_exam_qstn						
	   SET QSTN_CTS	= #{qstnCts}					
	   WHERE  CRS_CRE_CD 	= #{crsCreCd}				
	   AND  EXAM_SN 		= #{examSn}				
	   AND  EXAM_QSTN_SN 	= #{examQstnSn}


UPDATE 	tb_lec_exam_qstn										
	      SET 	QSTN_NO			= #{qstnNo}								
	    WHERE  CRS_CRE_CD     	= #{crsCreCd}								
	      AND  EXAM_SN        	= #{examSn}								
	      AND  EXAM_QSTN_SN   	= #{examQstnSn}
