mapper.update:

UPDATE	tb_lec_forum_join_user								
	   SET	SCORE  		= #{score}									
	   	 ,	MOD_NO  	= #{modNo}
	     ,	MOD_DTTM  	= DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	 WHERE	CRS_CRE_CD	= #{crsCreCd}								
	   AND	FORUM_SN	= #{forumSn}								
	   AND	STD_NO		= #{stdNo}

mapper.delete:

DELETE  FROM tb_lec_forum_join_user     
    WHERE  CRS_CRE_CD	= #{crsCreCd}    		
      AND  FORUM_SN	= #{forumSn}
