mapper.sql:
SELECT 

FROM tb_olc_lesn_cartrg A 
WHERE ORG_CD = #{orgCd}

mapper.insert:

	 INSERT INTO tb_olc_lesn_cartrg ( 
	         CARTRG_CD 
	       , ORG_CD 
	       , USER_NO 
	       , CTGR_CD 
	       , CARTRG_NM 
	       , CARTRG_TAG 
	       , CARTRG_ODR 
	       , USE_YN 
	       , OPEN_YN 
	       , REG_NO 
	       , REG_DTTM 
	       , MOD_NO 
	       , MOD_DTTM 
	       , CNTS_SHARE_CD 
	       , KNOW_SHARE_CD 
	 ) VALUES ( 
 			 #{cartrgCd}
	       , #{orgCd} 
	       , #{userNo} 
	       , #{ctgrCd} 
	       , #{cartrgNm} 
	       , #{cartrgTag} 
	       , ( SELECT NVL((MAX(CARTRG_ODR)),0) + 1
	             FROM tb_olc_lesn_cartrg AS SUBQUERY 
	            WHERE ORG_CD = #{orgCd 
	              AND USER_NO = #{userNo 
	              AND CTGR_CD = #{ctgrCd} ) 
	       , #{useYn} 
	       , #{openYn} 
	       , #{regNo} 
	       , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	       , #{modNo} 
	       , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	       , #{cntsShareCd} 
	       , #{knowShareCd} 
	 )

mapper.update:

UPDATE tb_olc_lesn_cartrg 
     SET CTGR_CD = #{ctgrCd} 
       , CARTRG_NM = #{cartrgNm} 
       , CARTRG_TAG = #{cartrgTag} 
       , CARTRG_ODR = #{cartrgOdr} 
       , USE_YN = #{useYn} 
       , OPEN_YN = #{openYn}
       , MOD_NO = #{modNo}
       , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
       , KNOW_OPEN_YN = #{knowOpenYn} 
   WHERE CARTRG_CD = #{cartrgCd} 
     AND ORG_CD = #{orgCd}

update.foreach:
UPDATE tb_olc_lesn_cartrg
SET
	 CTGR_CD = #{item.ctgrCd} 
  , CARTRG_NM = #{item.cartrgNm} 
  , CARTRG_TAG = #{item.cartrgTag} 
  , CARTRG_ODR = #{item.cartrgOdr} 
  , USE_YN = #{item.useYn} 
  , OPEN_YN = #{item.openYn}
  , MOD_NO = #{item.modNo}
  , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
  , KNOW_OPEN_YN = #{item.knowOpenYn} 
 WHERE CARTRG_CD = #{item.cartrgCd}

mapper.delete:

DELETE FROM tb_olc_lesn_cartrg 
  WHERE CARTRG_CD = #{cartrgCd} 
    AND ORG_CD = #{orgCd}

mapper.update:

