       , KNOW_SHARE_CD = #{knowShareCd}
       , OPEN_YN	   = #{openYn} 	  
       , KNOW_OPEN_YN  = #{knowOpenYn}  
   WHERE CARTRG_CD = #{cartrgCd} 		  
     AND ORG_CD = #{orgCd}


