mapper.sql:
SELECT 

FROM tb_olc_lesn_cnts A 
WHERE ORG_CD = #{orgCd}

mapper.select:
SELECT 
	COUNT(*)
FROM tb_olc_lesn_cnts A 
WHERE ORG_CD = #{orgCd} 

AND CARTRG_CD = #{cartrgCd}

mapper.insert:


INSERT INTO tb_olc_lesn_cnts ( 
         CNTS_CD 
       , ORG_CD 
       , USER_NO 
       , CARTRG_CD 
       , CNTS_NM 
       , CNTS_ODR 
       , CNTS_DIV 
       , CNTS_CTS 
       , REG_NO 
       , REG_DTTM 
       , MOD_NO 
       , MOD_DTTM 
       , CNTS_FILE_PATH 
 ) VALUES ( 
		 #{cntsCd}
       , #{orgCd} 
       , #{userNo}
       , #{cartrgCd}
       , #{cntsNm} 
       , ( SELECT NVL((MAX(CNTS_ODR)),0) + 1 
             FROM tb_olc_lesn_cnts AS SUBQUERY
            WHERE ORG_CD = #{orgCd} 
              AND USER_NO = #{userNo} 
              AND CARTRG_CD = #{cartrgCd} ) 
       , #{cntsDiv} 
       , #{cntsCts} 
       , #{regNo} 
        , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
       , #{modNo} 
	   , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s') 
       , #{cntsFilePath} 
 )

mapper.update:

UPDATE tb_olc_lesn_cnts 
     SET CARTRG_CD = #{cartrgCd} 
       , CNTS_NM = #{cntsNm} 
       , CNTS_ODR = #{cntsOdr} 
       , CNTS_DIV = #{cntsDiv} 
       , CNTS_CTS = #{cntsCts} 
       , REG_NO = #{regNo} 
       , REG_DTTM = #{regDttm} 
       , MOD_NO = #{modNo} 
       , MOD_DTTM = #{modDttm} 
       , CNTS_FILE_PATH = #{cntsFilePath} 
   WHERE CNTS_CD = #{cntsCd} 
     AND ORG_CD = #{orgCd}

update.foreach:
	UPDATE tb_olc_lesn_cnts 
    SET
     	CARTRG_CD = #{item.cartrgCd} 
       , CNTS_NM = #{item.cntsNm} 
       , CNTS_ODR = #{item.cntsOdr} 
       , CNTS_DIV = #{item.cntsDiv} 
       , CNTS_CTS = #{item.cntsCts} 
       , REG_NO = #{item.regNo} 
       , REG_DTTM = #{item.regDttm} 
       , MOD_NO = #{item.modNo} 
       , MOD_DTTM = #{item.modDttm} 
       , CNTS_FILE_PATH = #{item.cntsFilePath}
      WHERE CNTS_CD = #{item.cntsCd} 
     	AND ORG_CD = #{item.orgCd}

mapper.delete:

DELETE FROM tb_olc_lesn_cnts 
   WHERE CNTS_CD = #{cntsCd} 
     AND ORG_CD = #{orgCd}
