mapper.insert:

INSERT INTO tb_cro_open_crs_sbj (  
         CRS_CD  
       , SBJ_CD  
       , SBJ_ODR  
       , REG_NO  
       , REG_DTTM  
       , MOD_NO  
       , MOD_DTTM  
  ) VALUES (  
         #{crsCd}  
       , #{sbjCd}  
       , ( SELECT NVL((MAX(SBJ_ODR)),0)+1
             FROM tb_cro_open_crs_sbj AS SUBQUERY 
            WHERE SBJ_CD = #{sbjCd} )  
       , #{regNo}  
       , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
       , #{modNo}  
       , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
  )

mapper.update:

UPDATE tb_cro_open_crs_sbj  
   SET SBJ_ODR = #{sbjOdr}  
     , MOD_NO = #{modNo}  
     , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
 WHERE CRS_CD = #{crsCd}  
   AND SBJ_CD = #{sbjCd}

update.foreach:
	UPDATE tb_cro_open_crs_sbj  
    SET
     	 SBJ_ODR = #{item.sbjOdr}  
       , MOD_NO = #{item.modNo}  
       , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
   WHERE CRS_CD = #{item.crsCd}  
     AND SBJ_CD = #{item.sbjCd}

mapper.delete:

DELETE FROM tb_cro_open_crs_sbj  
   WHERE CRS_CD = #{crsCd}  
     AND SBJ_CD = #{sbjCd}
