mapper.select:
		SELECT 
			COUNT(*)
        FROM tb_cro_open_crs A, tb_cro_open_crs_ctgr B  
        WHERE A.CTGR_CD = B.CTGR_CD  
	      AND B.ORG_CD = #{orgCd}  	
	      AND A.CTGR_CD = #{ctgrCd}  

			AND A.EXPOSURE_YN = #{exposureYn}

mapper.insert:


INSERT INTO tb_cro_open_crs (  
         CRS_CD  
       , CTGR_CD  
       , CRS_NM  
       , CRS_DESC  
       , CRS_IMG  
       , CRS_IMG_DESC  
       , USER_LVL_CD  
       , USE_YN  
       , CRS_ODR  
       , EXPOSURE_YN  
       , REG_NO  
       , REG_DTTM  
       , MOD_NO  
       , MOD_DTTM  
  ) VALUES (  
		 #{crsCd}
       , #{ctgrCd}
       , #{crsNm}  
       , #{crsDesc}  
       , #{crsImg}  
       , #{crsImgDesc}  
       , #{userLvlCd} 
       , #{useYn}  
       , ( SELECT NVL((MAX(CRS_ODR)),0)+1  
             FROM tb_cro_open_crs AS SUBQUERY  
            WHERE CTGR_CD = #{ctgrCd} )  
       , #{exposureYn}  
       , #{regNo}  
       , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
       , #{modNo}  
       , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
  )

mapper.update:


UPDATE tb_cro_open_crs  
     SET CTGR_CD = #{ctgrCd}  
       , CRS_NM = #{crsNm}  
       , CRS_DESC = #{crsDesc}  
       , CRS_IMG = #{crsImg}  
       , CRS_IMG_DESC = #{crsImgDesc}  
       , USER_LVL_CD = #{userLvlCd}  
       , USE_YN = #{useYn}  
       , CRS_ODR = #{crsOdr}  
       , EXPOSURE_YN = #{exposureYn}  
       , MOD_NO = #{modNo}  
       , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
   WHERE CRS_CD = #{crsCd}

update.foreach:
	UPDATE tb_cro_open_crs  
    SET
     	 CTGR_CD = #{item.ctgrCd}  
       , CRS_NM = #{item.crsNm}  
       , CRS_DESC = #{item.crsDesc}  
       , CRS_IMG = #{item.crsImg}  
       , CRS_IMG_DESC = #{item.crsImgDesc}  
       , USER_LVL_CD = #{item.userLvlCd}  
       , USE_YN = #{item.useYn}  
       , CRS_ODR = #{item.crsOdr}  
       , EXPOSURE_YN = #{item.exposureYn}  
       , MOD_NO = #{item.modNo}  
       , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	WHERE CRS_CD = #{item.crsCd}
