		 설  명 : 기관 도메인의 전체 카운트
 	*/
 	SELECT COUNT(*) FROM (

 	) T

		/*
			 SQL ID : select
			 설  명 : 기관 도메인의 정보 조회
	 	*/

           AND ORG_DOMAIN = #{orgDomain}

mapper.insert:
	/*
		 SQL ID : insert
		 설  명 : 기관 도메인의 정보 등록
 	*/
	INSERT INTO tb_org_domain (
	       ORG_CD
	     , ORG_DOMAIN
	     , DFLT_YN
	     , RPRST_YN
	     , REG_NO
	     , REG_DTTM
	     , MOD_NO
	     , MOD_DTTM
	) VALUES (
	       #{orgCd}
	     , #{orgDomain}
	     , #{dfltYn}
	     , #{rprstYn}
	     , #{regNo}
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	     , #{modNo}
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	)

mapper.update:
	/*
		 SQL ID : update
		 설  명 : 기관 도메인의 정보 수정
 	*/
	UPDATE tb_org_domain
	   SET DFLT_YN = #{dfltYn}
	     , RPRST_YN = #{rprstYn}
	     , MOD_NO = #{modNo}
	     , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	 WHERE ORG_CD = #{orgCd}
	   AND ORG_DOMAIN = #{orgDomain}

mapper.delete:
	/*
		 SQL ID : delete
		 설  명 : 기관 도메인의 정보 삭제
 	*/
	DELETE FROM tb_org_domain
	 WHERE ORG_CD = #{orgCd}
	   AND ORG_DOMAIN = #{orgDomain}

mapper.update:
	/*
		 SQL ID : updateRprstN
		 설  명 : 기관의 대표 도베인을 N 상태로 변경
 	*/
	UPDATE tb_org_domain
	   SET RPRST_YN = 'N'
	     , MOD_NO = #{modNo}
	     , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	 WHERE ORG_CD = #{orgCd}

	/*
		 SQL ID : updateRprstY
		 설  명 : 기관의 대표 도베인 지정
 	*/
	UPDATE tb_org_domain
	   SET RPRST_YN = 'Y'
	     , MOD_NO = #{modNo}
	     , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	 WHERE ORG_CD = #{orgCd}
	   AND ORG_DOMAIN = #{orgDomain}
