mapper.insert:
	/*
		 SQL ID : insert
		 설  명 : 기관 이미지의 정보 등록
 	*/
	INSERT INTO tb_org_img_info (
	       ORG_CD
	     , IMG_SN
	     , IMG_TYPE_CD
	     , BKGR_FILE_SN
	     , BKGR_IMG_NM
	     , BKGR_FILE_URL
	     , DESC_FILE_SN
	     , DESC_IMG_NM
	     , DESC_FILE_URL
	     , CONN_FILE_SN
	     , CONN_URL
	     , CONN_TRGT
	     , IMG_ODR
	     , REG_NO
	     , REG_DTTM
	     , MOD_NO
	     , MOD_DTTM
	     , IMG_TITLE
	) VALUES (
	       #{orgCd}
	     , #{imgSn}
	     , #{imgTypeCd}
	     , #{bkgrFileSn}
	     , #{bkgrImgNm}
	     , #{bkgrFileUrl}
	     , #{descFileSn}
	     , #{descImgNm}
	     , #{descFileUrl}
	     , #{connFileSn}
	     , #{connUrl}
	     , #{connTrgt}
	     , ( SELECT MAX(NVL((IMG_ODR),0)) + 1
	           FROM tb_org_img_info AS SUBQUERY
	          WHERE ORG_CD = #{orgCd}
	            AND IMG_TYPE_CD = #{imgTypeCd} )
	     , #{regNo}
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	     , #{modNo}
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	     , #{imgTitle}
	)

mapper.update:
	/*
		 SQL ID : update
		 설  명 : 기관 이미지의 정보 수정
 	*/
	UPDATE tb_org_img_info
	   SET BKGR_FILE_SN = #{bkgrFileSn}
	     , BKGR_IMG_NM = #{bkgrImgNm}
	     , BKGR_FILE_URL = #{bkgrFileUrl}
	     , DESC_FILE_SN = #{descFileSn}
	     , DESC_IMG_NM = #{descImgNm}
	     , DESC_FILE_URL = #{descFileUrl}
	     , CONN_FILE_SN = #{connFileSn}
	     , CONN_URL = #{connUrl}
	     , CONN_TRGT = #{connTrgt}
	     , IMG_ODR = #{imgOdr}
	     , MOD_NO = #{modNo}
	     , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	     , IMG_TITLE = #{imgTitle}
	 WHERE ORG_CD = #{orgCd}
	   AND IMG_SN = #{imgSn}

mapper.delete:
	/*
		 SQL ID : delete
		 설  명 : 기관 이미지의 정보 삭제
 	*/
	DELETE FROM tb_org_img_info
	 WHERE ORG_CD = #{orgCd}
	   AND IMG_SN = #{imgSn}

	/*
		 SQL ID : deleteAll
		 설  명 : 기관 이미지 정보의 전체 삭제
 	*/
	DELETE FROM tb_org_img_info
	 WHERE ORG_CD = #{orgCd}
