ORdER BY ORG_CD, PAGE_ODR

	/*
		 SQL ID : count
		 설  명 : 기관 페이지의 전체 카운트
 	*/
 	SELECT COUNT(*) FROM (

 	) T

		/*
			 SQL ID : select
			 설  명 : 기관 페이지의 정보 조회
	 	*/

	 	   AND ORG_CD = #{orgCd}
           AND PAGE_CD = #{pageCd}

mapper.insert:
	/*
		 SQL ID : insert
		 설  명 : 기관 페이지의 정보 등록
 	*/
	INSERT INTO tb_org_page (
	       ORG_CD
	     , PAGE_CD
	     , PAGE_NM
	     , PAGE_CTS
	     , PAGE_ODR
	     , USE_YN
	     , DFLT_YN
	     , MENU_CD
	     , EDITOR_YN
	     , REG_NO
	     , REG_DTTM
	     , MOD_NO
	     , MOD_DTTM
	) VALUES (
	       #{orgCd}
	     , #{pageCd}
	     , #{pageNm}
	     , #{pageCts}
	     , ( SELECT NVL((MAX(PAGE_ODR)),0)+1 
	           FROM tb_org_page AS SUBQUERY
	          WHERE ORG_CD = #{orgCd} )
	     , #{useYn}
	     , #{dfltYn}
	     , #{menuCd}
	     , #{editorYn}
	     , #{regNo}
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	     , #{modNo}
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	)

mapper.update:
	/*
		 SQL ID : update
		 설  명 : 기관 페이지의 정보 수정
 	*/
	UPDATE tb_org_page
	   SET PAGE_NM = #{pageNm}
	     , PAGE_CTS = #{pageCts}
	     , PAGE_ODR = #{pageOdr}
	     , USE_YN = #{useYn}
	     , EDITOR_YN = #{editorYn}
	     , MOD_NO = #{modNo}
	     , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	 WHERE ORG_CD = #{orgCd}
	   AND PAGE_CD = #{pageCd}

mapper.delete:
	/*
		 SQL ID : delete
		 설  명 : 기관 페이지의 정보 삭제
 	*/
	DELETE FROM tb_org_page
	 WHERE ORG_CD = #{orgCd}
	   AND PAGE_CD = #{pageCd}

	/*
		 SQL ID : deleteAll
		 설  명 : 기관의 페이지 전체 정보 삭제
 	*/
	DELETE FROM tb_org_page
	 WHERE ORG_CD = #{orgCd}

mapper.update:
	/*
		 SQL ID : updateMenuCd
		 설  명 : 메뉴 코드와 연결
 	*/
	UPDATE tb_org_page
	   SET MENU_CD = #{menuCd}
	     , MOD_NO = #{modNo}
	     , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	 WHERE ORG_CD = #{orgCd}
	   AND PAGE_CD = #{pageCd}

	/*
		 SQL ID : updateMenuCd
		 설  명 : 메뉴 코드와 연결 해제
 	*/
	UPDATE tb_org_page
	   SET MENU_CD = ''
	     , MOD_NO = #{modNo}
	     , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	 WHERE ORG_CD = #{orgCd}
	   AND MENU_CD = #{menuCd}

	/*
		 SQL ID : updateMenuCd
		 설  명 : Default 가 아닌 모든 메뉴 코드와 연결 해제
 	*/
	UPDATE tb_org_page
	   SET MENU_CD = ''
	     , MOD_NO = #{modNo}
	     , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	 WHERE ORG_CD = #{orgCd}
	   AND DFLT_YN = 'N'
