mapper.select:
	/*
		 SQL ID : list
		 설  명 : 시스템 권한의 전체 목록
 	*/


	   AND USE_YN = #{useYn}

	 ORDER BY AUTH_GRP_ODR

		/*
			 SQL ID : select
			 설  명 : 시스템 권한의 정보 조회
	 	*/

           AND AUTH_GRP_CD = #{authGrpCd}

mapper.insert:
	/*
		 SQL ID : insert
		 설  명 : 시스템 권한의 정보 등록
 	*/
	INSERT INTO tb_sys_auth_grp (
	       MENU_TYPE
	     , AUTH_GRP_CD
	     , AUTH_GRP_NM
	     , USE_YN
	     , AUTH_GRP_DESC
	     , AUTH_GRP_ODR
	     , REG_NO
	     , REG_DTTM
	     , MOD_NO
	     , MOD_DTTM
	) VALUES ( 
	       #{menuType}
	     , #{authGrpCd}
	     , #{authGrpNm}
	     , #{useYn}
	     , #{authGrpDesc}
	     , ( SELECT NVL((MAX(AUTH_GRP_ODR)),0)+1
	           FROM tb_sys_auth_grp AS SUBQUERY 
	          WHERE MENU_TYPE = #{menuType} )
	     , #{regNo}
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	     , #{modNo}
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	)

mapper.update:
	/*
		 SQL ID : update
		 설  명 : 시스템 권한의 정보 수정
 	*/
	UPDATE tb_sys_auth_grp
	   SET AUTH_GRP_NM = #{authGrpNm}
	     , USE_YN = #{useYn}
	     , AUTH_GRP_DESC = #{authGrpDesc}
	     , AUTH_GRP_ODR = #{authGrpOdr}
	     , MOD_NO = #{modNo}
	     , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	 WHERE MENU_TYPE = #{menuType}
	   AND AUTH_GRP_CD = #{authGrpCd}

mapper.delete:
	/*
		 SQL ID : delete
		 설  명 : 시스템 권한의 정보 삭제
 	*/
	DELETE FROM tb_sys_auth_grp
	 WHERE MENU_TYPE = #{menuType}
	   AND AUTH_GRP_CD = #{authGrpCd}
