mapper.insert:
	/*
		 SQL ID : insert
		 설  명 : 코드의 정보 등록
 	*/
	INSERT INTO tb_sys_code (
	       CODE_CTGR_CD
	     , CODE_CD
	     , CODE_NM
	     , CODE_ODR
	     , CODE_DESC
	     , CODE_OPTN
	     , USE_YN
	     , REG_NO
	     , REG_DTTM
	     , MOD_NO
	     , MOD_DTTM
	) VALUES (			     
	       #{codeCtgrCd}
	     , #{codeCd}
	     , #{codeNm}
		 , ( SELECT NVL((MAX(CODE_ODR)),0)+1
		       FROM tb_sys_code AS SUBQUERY
		      WHERE CODE_CTGR_CD = #{codeCtgrCd} )		     
	     , #{codeDesc}
	     , #{codeOptn}
	     , #{useYn}
	     , #{regNo}
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	     , #{modNo}
	     , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	)

mapper.update:
	/*
		 SQL ID : update
		 설  명 : 코드의 정보 수정
 	*/
	UPDATE tb_sys_code
	   SET CODE_NM = #{codeNm}
	     , CODE_ODR = #{codeOdr}
	     , CODE_DESC = #{codeDesc}
	     , CODE_OPTN = #{codeOptn}
	     , USE_YN = #{useYn}
	     , MOD_NO = #{modNo}
	     , MOD_DTTM = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
	 WHERE CODE_CTGR_CD = #{codeCtgrCd}
	   AND CODE_CD = #{codeCd}

mapper.delete:
	/*
		 SQL ID : delete
		 설  명 : 코드의 정보 삭제
 	*/	
	DELETE FROM tb_sys_code
	 WHERE CODE_CTGR_CD = #{codeCtgrCd}
	   AND CODE_CD = #{codeCd}

	/*
		 SQL ID : deleteAll
		 설  명 : 분류 하위의 전체 코드 정보를 삭제
 	*/	
	DELETE FROM tb_sys_code
	 WHERE CODE_CTGR_CD = #{codeCtgrCd}
