mapper.update:
UPDATE tb_tch_insd_tch_sch_detl    
SET    YEAR        = #{year},        
       MONTH       = #{month},       
       DAY         = #{day},         
       START_TM    = #{startTm},     
       END_TM      = #{endTm}        
WHERE  USER_NO     = #{userNo}       
AND    SCH_SN      = #{schSn}        
AND    SCH_DETL_SN = #{schDetlSn}
