mapper.select:


	 	AND  A.TCH_TYPE      = #{tchType}

		AND  A.CRS_CRE_CD 	= #{crsCreCd} 								
 ORDER  BY TCH_ODR

mapper.insert:
 	INSERT  INTO TB_CRS_CRE_CRS_TCH ( 			
 	   		CRS_CRE_CD							
        ,  USER_NO								
        ,  TCH_TYPE							
        ,  TCH_ODR								
        ,  TCH_RATE_SCORE						
        ,  DECLS_NO							
        ,  REG_NO								
        ,  REG_DTTM							
        ,  MOD_NO								
        ,  MOD_DTTM )							
	SELECT 	#{crsCreCd}							
        ,  #{userNo}								
        ,  #{tchType}							
        ,  NVL(MAX(TCH_ODR),0)+1				
        ,  #{tchRateScore}						
        ,  #{declsNo}							
        ,  #{regNo}								
        ,  TO_CHAR(SYSDATE,'yyyymmddhh24miss')	
        ,  #{modNo}								
        ,  TO_CHAR(SYSDATE,'yyyymmddhh24miss')	
     FROM  TB_CRS_CRE_CRS_TCH					
    WHERE  CRS_CRE_CD = #{crsCreCd}

mapper.update:
	UPDATE  TB_CRS_CRE_CRS_TCH 	 									
      SET  TCH_ODR 		= #{tchOdr}								
        ,  TCH_RATE_SCORE 	= #{tchRateScore}							
        ,  MOD_NO 			= #{modNo}								
        ,  MOD_DTTM 		= TO_CHAR(SYSDATE,'yyyymmddhh24miss')	
  	 WHERE  CRS_CRE_CD 		= #{crsCreCd}								
      AND  USER_NO 		= #{userNo}

update.foreach:
	  UPDATE  TB_CRS_CRE_CRS_TCH 	
	  SET
	           TCH_ODR 		= #{item.tchOdr}								
	        ,  TCH_RATE_SCORE 	= #{item.tchRateScore}							
	        ,  MOD_NO 			= #{item.modNo}								
	        ,  MOD_DTTM 		= TO_CHAR(SYSDATE,'yyyymmddhh24miss')	  
  	 WHERE  CRS_CRE_CD 		= #{item.crsCreCd}								
      	AND  USER_NO 		= #{item.userNo}

mapper.delete:
	DELETE  FROM TB_CRS_CRE_CRS_TCH			
  	WHERE  CRS_CRE_CD	= #{crsCreCd}			
      	AND  USER_NO 	= #{userNo}
