mapper.select:
	/*
		 SQL ID : count
		 설  명 : 오류 로그 페이징 카운트
 	*/
 	SELECT COUNT(*) FROM (

 	) T

mapper.insert:
		/*
			 SQL ID : insert
			 설  명 : 오류 로그 정보 등록
	 	*/
	 	INSERT INTO TB_LOG_EXCEPTION (
	 	       EXCEPTION_DIV
		     , ORIGIN_DT
		     , METHOD_NM
		     , EXCEPTION_NM
		     , STACK_TRACE
		     , MOD_DTTM
		     , ORIGIN_CNT
	 	) VALUES (
               #{exceptionDiv}
             , #{originDt}
             , #{methodNm}
             , #{exceptionNm}
             , #{stackTrace}
             , TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS')
             , 1	 	
	 	)

mapper.update:
	/*
		 SQL ID : update
		 설  명 : 오류 로그의 정보 수정
 	*/
 	UPDATE TB_LOG_EXCEPTION
 	   SET ORIGIN_CNT = ORIGIN_CNT + 1
	     , MOD_DTTM = TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS')
	 WHERE EXCEPTION_DIV = #{exceptionDiv}
	   AND ORIGIN_DT = #{originDt}
	   AND METHOD_NM = #{methodNm}
	   AND EXCEPTION_NM = #{exceptionNm}
