           AND CODE_CTGR_CD = #{codeCtgrCd}

mapper.insert:
	/*
		 SQL ID : insert
		 설  명 : 코드 분류의 정보 등록
 	*/
	INSERT INTO TB_SYS_CODE_CTGR (
	       CODE_CTGR_CD
	     , CODE_CTGR_NM
	     , CODE_CTGR_ODR
	     , CODE_CTGR_DESC
	     , USE_YN
	     , REG_NO 
	     , REG_DTTM
	     , MOD_NO
	     , MOD_DTTM
	) VALUES (
		   #{codeCtgrCd}
		 , #{codeCtgrNm}
		 , ( SELECT NVL(MAX(CODE_CTGR_ODR),0)+1
		       FROM TB_SYS_CODE_CTGR )
		 , #{codeCtgrDesc}
		 , #{useYn}
		 , #{regNo}
		 , TO_CHAR(SYSDATE,'yyyymmddhh24miss')
		 , #{modNo}
		 , TO_CHAR(SYSDATE,'yyyymmddhh24miss')
	)

mapper.update:
	/*
		 SQL ID : update
		 설  명 : 코드 분류의 정보 수정
 	*/
	UPDATE TB_SYS_CODE_CTGR
	   SET CODE_CTGR_NM = #{codeCtgrNm}
	     , CODE_CTGR_ODR = #{codeCtgrOdr}
	     , CODE_CTGR_DESC = #{codeCtgrDesc}
	     , USE_YN = #{useYn}
	     , MOD_NO = #{modNo}
	     , MOD_DTTM = TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS')
	 WHERE CODE_CTGR_CD = #{codeCtgrCd}

mapper.delete:
	/*
		 SQL ID : delete
		 설  명 : 코드 분류의 정보 삭제
 	*/
	DELETE FROM TB_SYS_CODE_CTGR
	 WHERE CODE_CTGR_CD = #{codeCtgrCd}
